 #include<bits/stdc++.h> using namespace std; int main() {    int t;    cin>>t;    int arr[10]={0};    string s;    cin>>s;    int l=0,r=9;    for(int i=0;i<t;i++)    {        if(s[i]=='L')        {            while (arr[l])l++;            arr[l]=1;        }        else if(s[i]=='R')        {            while(arr[r])r--;            arr[r]=1;        }        else        {           int x=s[i]-'0';           arr[x]=0;           l=min(x,l);           r=max(x,r);        }   }   for(int i=0;i<10;i++)   cout<<arr[i]; }