#include<iostream>using namespace std;int w[5]={1,10,100,1000,10000};void Round(int n){int num=n, k=0, i=0;while(num){		if(num%10) k++;			num/=10;		}		printf("%d\n", k);				while(n){		if(n%10){				printf("%d ", (n%10)*w[i]);			}			n/=10;			i++;	}		printf("\n");	}	int main(){		int t,n;		scanf("%d", &t);		while(t--){			scanf("%d", &n);					Round(n);		}		return 0;