#include<bits/stdc++.h>#define ll long long intusing namespace std;int main(){    int t;    cin>>t;    while(t--)    {        ll a,b,n,m;        cin>>a>>b>>n>>m;        if(a>b) swap(a,b);        if(a+b<m+n) cout<<"No"<<endl;        else if (a<m) cout<<"No"<<endl;        else cout<<"Yes"<<endl;    }}