#include<bits/stdc++.h> using namespace std; int main() {    int a;    cin>>a;    int arr[a];    for(int i=0;i<a;i++)    {        cin>>arr[i];    }    sort(arr,arr+a);    int num=0,ser=0;    for(int i=0;i<a;i++)    {        if(arr[i]>=ser)        {            ser+=arr[i];            num++;        }    }    cout<<num<<endl; }